# Model training pipeline
def train_model(features_df, labels, model_type="gradient_boosting"):
    """
    Train a machine learning model for golf parlay prediction

    Args:
        features_df: DataFrame containing engineered features
        labels: Series containing outcome labels
        model_type: Type of model to train

    Returns:
        Trained model object and performance metrics
    """
    # Split data
    X_train, X_test, y_train, y_test = train_test_split(
        features_df, labels, test_size=0.2, random_state=42
    )

    # Select and configure model
    if model_type == "gradient_boosting":
        model = GradientBoostingClassifier(
            n_estimators=100,
            learning_rate=0.1,
            max_depth=3
        )
    elif model_type == "neural_network":
        model = MLPClassifier(
            hidden_layer_sizes=(100, 50),
            activation="relu",
            solver="adam"
        )

    # Train model
    model.fit(X_train, y_train)

    # Evaluate performance
    y_pred = model.predict(X_test)
    y_prob = model.predict_proba(X_test)[:, 1]

    metrics = {
        "accuracy": accuracy_score(y_test, y_pred),
        "roc_auc": roc_auc_score(y_test, y_prob),
        "precision": precision_score(y_test, y_pred),
        "recall": recall_score(y_test, y_pred)
    }

    return model, metrics
