```sql
-- Create materialized view for player scoring averages
CREATE MATERIALIZED VIEW player_scoring_averages AS
SELECT 
  prs.dg_id,
  p.name AS player_name,
  COUNT(prs.round_score) AS rounds_count,
  AVG(prs.round_score) AS scoring_average,
  -- Last 10 rounds average
  (SELECT AVG(recent.round_score)
   FROM (
     SELECT round_score
     FROM player_round_scores
     WHERE dg_id = prs.dg_id AND round_score IS NOT NULL
     ORDER BY updated_at DESC
     LIMIT 10
   ) AS recent
  ) AS last10_average,
  -- Standard deviation of scores
  STDDEV(prs.round_score) AS score_stddev
FROM player_round_scores prs
JOIN players p ON prs.dg_id = p.dg_id
WHERE prs.round_score IS NOT NULL
GROUP BY prs.dg_id, p.name
HAVING COUNT(prs.round_score) >= 4;

-- Create index on the materialized view
CREATE INDEX idx_player_scoring_averages_dg_id ON player_scoring_averages(dg_id);
CREATE INDEX idx_player_scoring_averages_scoring_average ON player_scoring_averages(scoring_average);
