```javascript
class PlayerTrendService {
  constructor(db) {
    this.db = db;
  }

  async getPlayerScoringTrend(dgId, months = 6) {
    const query = {
      text: `
        SELECT 
          t.event_id,
          t.event_name,
          t.start_date,
          t.course_name,
          t.course_par,
          prs.round_number,
          prs.round_score,
          -- Calculate score relative to par for display purposes only
          (prs.round_score - t.course_par) AS score_to_par
        FROM player_round_scores prs
        JOIN tournaments t ON prs.event_id = t.event_id
        WHERE prs.dg_id = $1
          AND prs.round_score IS NOT NULL
          AND t.start_date >= NOW() - INTERVAL '$2 months'
        ORDER BY t.start_date, prs.round_number
      `,
      values: [dgId, months]
    };

    const result = await this.db.query(query);

    // Calculate moving averages
    const rounds = result.rows;
    const movingAverages = this.calculateMovingAverages(rounds.map(r => r.round_score), 5);

    return {
      rounds: rounds,
      trend: {
        movingAverages: movingAverages,
        overallAverage: this.calculateAverage(rounds.map(r => r.round_score)),
        recentAverage: this.calculateAverage(rounds.slice(-10).map(r => r.round_score))
      }
    };
  }

  calculateMovingAverages(scores, window) {
    const result = [];
    for (let i = 0; i <= scores.length - window; i++) {
      const windowScores = scores.slice(i, i + window);
      const average = this.calculateAverage(windowScores);
      result.push(average);
    }
    return result;
  }

  calculateAverage(scores) {
    if (scores.length === 0) return null;
    return scores.reduce((sum, score) => sum + score, 0) / scores.length;
  }
}
