Example settlement service update (TypeScript):
```typescript
import { Pool } from 'pg';

const pool = new Pool({
  connectionString: process.env.DATABASE_URL
});

export class SettlementService {
  // Get tournament results for settlement
  async getTournamentResultsForSettlement(eventId: number): Promise<any[]> {
    // Updated query to use new schema
    const query = {
      text: `
        SELECT 
          tr.dg_id,
          tr.player_name,
          tr.final_position,
          tr.total_score,
          tr.made_cut,
          t.event_name,
          t.course_par
        FROM tournament_results tr
        JOIN tournaments t ON tr.event_id = t.event_id
        WHERE tr.event_id = $1
        ORDER BY tr.final_position NULLS LAST
      `,
      values: [eventId]
    };

    const result = await pool.query(query);
    return result.rows;
  }

  // Settle a parlay based on tournament results
  async settleParlay(parlayId: number): Promise<boolean> {
    // Start transaction
    const client = await pool.connect();

    try {
      await client.query('BEGIN');

      // Get parlay details - using existing parlay tables
      const parlayQuery = {
        text: 'SELECT * FROM parlays WHERE id = $1',
        values: [parlayId]
      };
      const parlayResult = await client.query(parlayQuery);
      const parlay = parlayResult.rows[0];

      if (!parlay) {
        throw new Error(`Parlay with ID ${parlayId} not found`);
      }

      // Get parlay selections
      const selectionsQuery = {
        text: 'SELECT * FROM parlay_selections WHERE parlay_id = $1',
        values: [parlayId]
      };
      const selectionsResult = await client.query(selectionsQuery);
      const selections = selectionsResult.rows;

      // Process each selection
      let allWin = true;
      for (const selection of selections) {
        const eventId = selection.event_id;
        const dgId = selection.dg_id;
        const betType = selection.bet_type;

        // Get tournament result for this player
        const resultQuery = {
          text: 'SELECT * FROM tournament_results WHERE event_id = $1 AND dg_id = $2',
          values: [eventId, dgId]
        };
        const resultResult = await client.query(resultQuery);
        const playerResult = resultResult.rows[0];

        if (!playerResult) {
          console.warn(`No result found for player ${dgId} in event ${eventId}`);
          allWin = false;
          continue;
        }

        // Determine if selection is a win based on bet type
        let isWin = false;
        switch (betType) {
          case 'MAKE_CUT':
            isWin = playerResult.made_cut === true;
            break;
          case 'TOP_5':
            isWin = playerResult.final_position <= 5;
            break;
          case 'TOP_10':
            isWin = playerResult.final_position <= 10;
            break;
          case 'TOP_20':
            isWin = playerResult.final_position <= 20;
            break;
          case 'WINNER':
            isWin = playerResult.final_position === 1;
            break;
          default:
            console.warn(`Unknown bet type: ${betType}`);
            allWin = false;
        }

        // Update selection result
        await client.query(
          'UPDATE parlay_selections SET result = $1, settled_at = NOW() WHERE id = $2',
          [isWin ? 'WIN' : 'LOSS', selection.id]
        );

        if (!isWin) {
          allWin = false;
        }
      }

      // Update parlay status
      await client.query(
        'UPDATE parlays SET status = $1, settled_at = NOW() WHERE id = $2',
        [allWin ? 'WIN' : 'LOSS', parlayId]
      );

      await client.query('COMMIT');
      return allWin;
    } catch (error) {
      await client.query('ROLLBACK');
      console.error('Error settling parlay:', error);
      throw error;
    } finally {
      client.release();
    }
  }
}
