```python
import psycopg2
import pandas as pd
from tabulate import tabulate

conn = psycopg2.connect("dbname=golf_db user=postgres")

def validate_data_migration():
    with conn.cursor() as cur:
        # Check tournament counts
        cur.execute("SELECT COUNT(*) FROM tournaments")
        tournament_count = cur.fetchone()[0]

        # Check player counts
        cur.execute("SELECT COUNT(*) FROM players")
        player_count = cur.fetchone()[0]

        # Check round scores
        cur.execute("SELECT COUNT(*) FROM player_round_scores")
        round_scores_count = cur.fetchone()[0]

        # Check tournament results
        cur.execute("SELECT COUNT(*) FROM tournament_results")
        results_count = cur.fetchone()[0]

        # Validate scoring averages
        cur.execute("""
            SELECT 
                tr.dg_id, 
                tr.player_name,
                tr.scoring_average AS stored_average,
                ROUND(AVG(prs.round_score)::numeric, 2) AS calculated_average,
                ABS(tr.scoring_average - ROUND(AVG(prs.round_score)::numeric, 2)) AS difference
            FROM tournament_results tr
            JOIN player_round_scores prs ON tr.event_id = prs.event_id AND tr.dg_id = prs.dg_id
            GROUP BY tr.event_id, tr.dg_id, tr.player_name, tr.scoring_average
            HAVING ABS(tr.scoring_average - ROUND(AVG(prs.round_score)::numeric, 2)) > 0.01
        """)
        scoring_discrepancies = cur.fetchall()

        # Check for data integrity issues
        cur.execute("""
            SELECT 
                event_id, 
                dg_id, 
                player_name,
                COUNT(*) AS duplicate_count
            FROM tournament_results
            GROUP BY event_id, dg_id, player_name
            HAVING COUNT(*) > 1
        """)
        duplicates = cur.fetchall()

        # Print validation report
        print("\n=== Data Migration Validation Report ===")
        print(f"Tournaments: {tournament_count}")
        print(f"Players: {player_count}")
        print(f"Round Scores: {round_scores_count}")
        print(f"Tournament Results: {results_count}")

        if scoring_discrepancies:
            print("\n=== Scoring Average Discrepancies ===")
            print(tabulate(scoring_discrepancies, 
                          headers=['DG ID', 'Player', 'Stored Avg', 'Calculated Avg', 'Difference']))
        else:
            print("\nNo scoring average discrepancies found.")

        if duplicates:
            print("\n=== Duplicate Records ===")
            print(tabulate(duplicates, 
                          headers=['Event ID', 'DG ID', 'Player', 'Count']))
        else:
            print("\nNo duplicate records found.")

if __name__ == "__main__":
    validate_data_migration()
    conn.close()
